def prepositional_phrase():
    preposition_list = [["about","above","according to","across","after","against","along","among","around","at","at the bottom of","at the top of","because of","behind","below","beneath","beside","before","between","beyond","by","close to","concerning","down","far from","from","higher than","in","in front of","in place of","in regard to","in spite of","inside","into","like","lower than","near","next to","off","on","on account of","onto","out","out of","outside","over","past","through","throughout","to","toward","under","up","upon","with","without"],["after","at","before","beyond","by","during","for","from","in","on","once upon","past","since","till","to","until"]]
    time_list = ["morning",str(random.randint(1,12))+" o'clock","a century","a time","afternoon","bedtime","dawn","day","daybreak","daytime","dinnertime","eternity","high noon","a jiffy","midday","midnight","night","noon","sunrise","sunset","the crack of dawn","the day","the hour","the minute","the moment","the second","time","tomorrow","yesterday",random.choice(["Monday","Tuesday","Wednesday","Thursday","Friday","Saturday","Sunday"]),random.choice(["January","February","March","April","May","June","July","August","September","October","November","December"])]
    
    if bool(random.getrandbits(1)):
        return random.choice(preposition_list[1]) + " " + random.choice(time_list)
    else:
        return random.choice(preposition_list[0]) + " the " + noun()
        
