def generator():
    length = random.randint(3,8)
    seed = []
    worded = [False,False]
    last = ""
    for each in range(0,length):
        if last == "A":
            single = random.choice(["A","A","C","N","N","N"])
        elif last == "C" or last == "N" :
            if worded[0]:
                single = "P"
            else:
                single = random.choice(["V","P"])
        elif last == "P":
            single = random.choice(["A","A","A","P","C","N","N","N"])
        else:
            single = random.choice(["A","A","A","P","C","N","N","N"])
        
        if single == "V":
            worded[0] = True
        elif single == "N" or single == "C":
            worded[1] = True
        seed.append(single)
        last = single
        
    if seed[len(seed)-1] == "A" and seed[len(seed)-2] == "A":
        seed[len(seed)-1] = "N"
    elif seed[len(seed)-1] == "A":
        seed[len(seed)-1] = "P"
        
    for each in range(0,length-1):
        if not worded[1]:
            if seed[each] == "A":
                seed[each+1] = "N"
                worded[1] = True
        if not worded[0]:
            if seed[each] == "N" or seed[each] == "C":
                seed[each+1] = "V"
                worded[0] = True
    for each in range(0,length-1):
        if not worded[1]:
            if seed[each] == "P":
                seed[each+1] = "N"
                worded[1] = True
        if not worded[0]:
            if seed[each] == "N" or seed[each] == "C":
                seed[each+1] = "V"
                worded[0] = True
    collect = False
    segmented = []
    segment = []
    for each in seed:
        if each == "A":
            segment.append(each)
            collect = True
        elif each == "N" or each == "C":
            segment.append(each)
            collect = False
        else:
            segment.append(each)
        if not collect:
            segmented.append(segment)
            segment = []
    return segmented

def define(char,person):
    if char == "A":
        return adjective()
    elif char == "N":
        return noun()
    elif char == "C":
        if(bool(random.getrandbits(1))):
            return person
        else:
            return character()
    elif char == "P":
        return prepositional_phrase()+","
    elif char == "V":
        return verbed()
def sentence(seed,person,first=False,second=False):
    sentence_str = ""
    verb_loc = 0
    if first:
        vague = adjective()+" "+noun()
        return random.choice([prepositional_phrase().capitalize()+" the "+vague+" "+verbed()+" for "+person+" "+prepositional_phrase()+". ", person+" never liked being "+adjective()+", but there always seemed to be the "+noun()+" "+prepositional_phrase()+". Over time "+person+" "+verbed()+" to "+verb()+" "+prepositional_phrase()+". ", person+" was known to be "+adjective()+", but the "+vague+" never "+verbed()+" "+prepositional_phrase()+" with them. The "+vague+" always "+verbed()+" because of the "+noun()+" "+prepositional_phrase()+". ", prepositional_phrase().capitalize()+" "+prepositional_phrase()+", this "+vague+" "+verbed()+" "+prepositional_phrase()+". This "+vague+" was known as "+person+". "])
    for location in range(0,len(seed)):
        if "V" in seed[location]:
            verb_loc = location
    for i in range(0,verb_loc):
        if "N" in seed[i] or "A" in seed[i]:
                sentence_str += "the "
        if ("N" in seed[i] or "C" in seed[i]) and ("N" in seed[i+1] or "C" in seed[i+1]):
            for j in range(0,len(seed[i])):
                if "A" == seed[i][j] and "A" == seed[i][j+1]:
                    sentence_str += define(seed[i][j],person)+" "+conjunction()+" "
                else:
                    sentence_str += define(seed[i][j],person)+" "
            sentence_str += "and "
        else:
            for m in range(0,len(seed[i])):
                if "A" == seed[i][m] and "A" == seed[i][m+1]:
                    sentence_str += define(seed[i][m],person)+" "+conjunction()+" "
                else:
                    sentence_str += define(seed[i][m],person)+" "
    for i in range(verb_loc,len(seed)):
        if "N" in seed[i] or "A" in seed[i]:
                sentence_str += random.choice(["with ","to ","for ","without ","because of "])+"the "
        if ("N" in seed[i] or "C" in seed[i]) and ("N" in seed[i-1] or "C" in seed[i-1]):
            sentence_str += "and "
            for j in range(0,len(seed[i])):
                if "A" == seed[i][j] and "A" == seed[i][j+1]:
                    sentence_str += define(seed[i][j],person)+" "+conjunction()+" "
                else:
                    sentence_str += define(seed[i][j],person)+" "
        else:
            for n in range(0,len(seed[i])):
                if "A" == seed[i][n] and "A" == seed[i][n+1]:
                    sentence_str += define(seed[i][n],person)+" "+conjunction()+" "
                else:
                    sentence_str += define(seed[i][n],person)+" "
    if random.randint(1,4) == 3:
        sentence_str = sentence_str.rstrip()
        if sentence_str.rfind(",") == len(sentence_str)-1:
            sentence_str = sentence_str[:-1]
        sentence_str += ", "+conjunction()+" "+sentence(generator(),person,False,True)
    if not second:
        sentence_str = sentence_str[:1].upper() + sentence_str[1:]
    sentence_str = sentence_str.rstrip()
    if sentence_str.rfind(",") == len(sentence_str)-1:
        sentence_str = sentence_str[:-1]
    if second:
        return sentence_str
    else:
        return sentence_str+"."
def paragraph(people=[], length=0):
    sentence_num = random.randint(5,15) if length == 0 else length
    if not people:
        character_names = [character()]
    else:
        character_names = people
    # introduction = ""
    full = ""

    for index in range(0,sentence_num):
        # if index>0:
        full += sentence(generator(),random.choice(character_names))+" "
        # else:
        #     introduction = sentence(generator(),random.choice(character_names),True)
        #     full += introduction
    # return [full,character_names,introduction]
    return [full,character_names]
